import React, { useState, useEffect } from 'react';
import { useParams, useLocation, useNavigate } from 'react-router-dom';
import { eventsAPI, eventCancellationAPI } from '../../services/api';

const RefundInitiation = () => {
  const { eventId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  
  const [event, setEvent] = useState(null);
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sending, setSending] = useState(false);
  const [stats, setStats] = useState({
    totalBookings: 0,
    totalUsers: 0,
    totalAmount: 0
  });

  useEffect(() => {
    fetchEventAndBookings();
  }, [eventId]);

  const fetchEventAndBookings = async () => {
    try {
      setLoading(true);
      
      // Fetch event details
      const eventResponse = await eventsAPI.getById(eventId);
      setEvent(eventResponse.data.event);

      // Fetch bookings for cancelled event
      const bookingsResponse = await fetch(`http://localhost:5000/api/bookings?event_id=${eventId}`, {
        credentials: 'include'
      });
      const bookingsData = await bookingsResponse.json();
      
      if (bookingsData.success) {
        const confirmedBookings = bookingsData.bookings.filter(b => b.status === 'confirmed');
        setBookings(confirmedBookings);
        
        // Calculate stats
        const uniqueUsers = new Set(confirmedBookings.map(b => b.user_id)).size;
        const totalAmount = confirmedBookings.reduce((sum, b) => sum + parseFloat(b.total_amount || 0), 0);
        
        setStats({
          totalBookings: confirmedBookings.length,
          totalUsers: uniqueUsers,
          totalAmount: totalAmount
        });
      }
    } catch (error) {
      console.error('Fetch error:', error);
      alert('Failed to load event details');
    } finally {
      setLoading(false);
    }
  };

  const handleInitiateRefunds = async () => {
    if (!window.confirm(`Are you sure you want to send refund emails to ${stats.totalUsers} attendees?`)) {
      return;
    }

    try {
      setSending(true);
      const response = await eventCancellationAPI.initiateRefunds(eventId);
      
      if (response.data.success) {
        alert(`‚úÖ Refund process initiated successfully!\n\n` +
              `‚Ä¢ ${response.data.refunds_created} refund requests created\n` +
              `‚Ä¢ ${response.data.emails_sent} emails sent to attendees\n` +
              `‚Ä¢ Total amount: $${response.data.total_amount}\n\n` +
              `Accounts team has been notified.`);
        
        navigate('/admin/refunds/tracking');
      }
    } catch (error) {
      console.error('Initiate refunds error:', error);
      alert(error.response?.data?.message || 'Failed to initiate refunds');
    } finally {
      setSending(false);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex items-center justify-center h-64">
          <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-primary-600"></div>
        </div>
      </div>
    );
  }

  if (!event || event.status !== 'cancelled') {
    return (
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="bg-yellow-50 border border-yellow-200 rounded-md p-4">
          <p className="text-yellow-800">Event not found or not cancelled.</p>
          <button
            onClick={() => navigate('/admin/events')}
            className="mt-4 text-primary-600 hover:text-primary-700"
          >
            ‚Üê Back to Events
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      {/* Header */}
      <div className="mb-8">
        <button
          onClick={() => navigate('/admin/events')}
          className="text-primary-600 hover:text-primary-700 mb-4 flex items-center"
        >
          ‚Üê Back to Events
        </button>
        <h1 className="text-3xl font-bold text-gray-900">Refund Initiation</h1>
        <p className="text-gray-600 mt-2">{event.title}</p>
      </div>

      {/* Event Info Card */}
      <div className="bg-white shadow rounded-lg p-6 mb-6">
        <div className="border-l-4 border-red-500 pl-4">
          <div className="flex items-center mb-2">
            <span className="px-3 py-1 bg-red-100 text-red-800 text-sm font-semibold rounded-full">
              CANCELLED
            </span>
          </div>
          <p className="text-sm text-gray-600 mb-1">
            <strong>Cancelled By:</strong> {event.cancelled_by_name || 'Admin'}
          </p>
          <p className="text-sm text-gray-600 mb-1">
            <strong>Cancelled On:</strong> {event.cancelled_at ? formatDate(event.cancelled_at) : 'Just now'}
          </p>
          <p className="text-sm text-gray-600">
            <strong>Reason:</strong> {event.cancellation_reason}
          </p>
        </div>
      </div>

      {/* Refund Summary */}
      <div className="bg-white shadow rounded-lg p-6 mb-6">
        <h2 className="text-xl font-semibold text-gray-900 mb-4">üìä Refund Summary</h2>
        
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
          <div className="bg-blue-50 p-4 rounded-lg">
            <div className="text-3xl font-bold text-blue-600">{stats.totalUsers}</div>
            <div className="text-sm text-gray-600">Attendees</div>
          </div>
          <div className="bg-green-50 p-4 rounded-lg">
            <div className="text-3xl font-bold text-green-600">{stats.totalBookings}</div>
            <div className="text-sm text-gray-600">Total Bookings</div>
          </div>
          <div className="bg-purple-50 p-4 rounded-lg">
            <div className="text-3xl font-bold text-purple-600">{formatCurrency(stats.totalAmount)}</div>
            <div className="text-sm text-gray-600">Total Refund Amount</div>
          </div>
        </div>

        {event.refund_initiated ? (
          <div className="bg-green-50 border border-green-200 rounded-md p-4 mb-4">
            <div className="flex items-center">
              <svg className="h-5 w-5 text-green-400 mr-2" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
              </svg>
              <div>
                <p className="text-sm font-medium text-green-800">
                  Refund emails have already been sent to all attendees
                </p>
                <p className="text-sm text-green-700 mt-1">
                  Sent on: {event.refund_initiated_at ? formatDate(event.refund_initiated_at) : 'Unknown'}
                </p>
              </div>
            </div>
          </div>
        ) : null}
      </div>

      {/* Affected Attendees List */}
      <div className="bg-white shadow rounded-lg p-6 mb-6">
        <h2 className="text-xl font-semibold text-gray-900 mb-4">üìã Affected Attendees</h2>
        
        {bookings.length === 0 ? (
          <p className="text-gray-500 text-center py-8">No confirmed bookings found for this event</p>
        ) : (
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Attendee</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Email</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Booking ID</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Tickets</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Refund Amount</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {bookings.map((booking) => (
                  <tr key={booking.booking_id}>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                      {booking.user_name || 'N/A'}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {booking.user_email || 'N/A'}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      #{booking.booking_id}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {booking.ticket_count || 1} ticket(s)
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-green-600">
                      {formatCurrency(booking.total_amount || 0)}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* Action Card */}
      {!event.refund_initiated && bookings.length > 0 && (
        <div className="bg-white shadow rounded-lg p-6">
          <h2 className="text-xl font-semibold text-gray-900 mb-4">üìß Initiate Refund Process</h2>
          
          <div className="bg-blue-50 border border-blue-200 rounded-md p-4 mb-6">
            <p className="text-sm text-blue-800 mb-2">
              <strong>This will:</strong>
            </p>
            <ul className="list-disc list-inside text-sm text-blue-700 space-y-1">
              <li>Send refund emails to {stats.totalUsers} attendees</li>
              <li>Create refund requests in the system</li>
              <li>Notify accounts team for processing</li>
              <li>Users will receive instructions to reply with bank details</li>
            </ul>
          </div>

          <div className="bg-yellow-50 border border-yellow-200 rounded-md p-4 mb-6">
            <div className="flex">
              <svg className="h-5 w-5 text-yellow-400 mr-2 mt-0.5" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
              </svg>
              <p className="text-sm text-yellow-800">
                <strong>Warning:</strong> This action cannot be undone. Once initiated, refund emails will be sent to all attendees.
              </p>
            </div>
          </div>

          <div className="flex justify-center">
            <button
              onClick={handleInitiateRefunds}
              disabled={sending}
              className="px-8 py-3 bg-primary-600 text-white text-lg font-semibold rounded-md hover:bg-primary-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
            >
              {sending ? (
                <>
                  <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                  Sending Emails...
                </>
              ) : (
                <>
                  üìß Send Refund Request Emails to All Attendees
                </>
              )}
            </button>
          </div>
        </div>
      )}

      {event.refund_initiated && (
        <div className="text-center">
          <button
            onClick={() => navigate('/admin/refunds/tracking')}
            className="px-6 py-3 bg-primary-600 text-white rounded-md hover:bg-primary-700"
          >
            View Refund Dashboard ‚Üí
          </button>
        </div>
      )}
    </div>
  );
};

export default RefundInitiation;
